type CardsProps = {
  sNo: number
  title: string
  description: string
  href: string
  navigate: NavigateFunction
  controls1: AnimationControls
  controls2: AnimationControls
}
const Card = (props: CardsProps) => {
  const animate = (path: number, dur: number) => {
    props.controls2.start({
      pathLength: path,
      transition: {
        duration: dur,
        delay: 0,
      },
    })
  }
  const svgDraw = () => {
    if (done) {
      return
    }
    done = true
    console.log('started')
    const dur = 3
    switch (props.sNo) {
      case 1:
        props.controls1.start({
          pathLength: 1,
          transition: {
            duration: dur / 2,
          },
        })
        props.controls2.start({
          pathLength: 0.05,
          transition: {
            delay: dur / 2,
            duration: dur / 2,
          },
        })
        break
      case 2:
        animate(0.14, dur)
        break
      case 3:
        animate(0.28, dur)
        break
      case 4:
        animate(0.43, dur)
        break
      case 5:
        animate(0.56, dur)
        break
      case 6:
        animate(0.63, dur)
        break
      case 7:
        animate(0.78, dur)
        break
      case 8:
        animate(0.88, dur)
        break
      case 9:
        animate(1, dur)
        break
    }
  }
  let done = false
  return (
    <motion.div
      viewport={{ once: true }}
      onViewportEnter={svgDraw}
      style={{
        ['--pos' as any]: props.sNo % 2 !== 0 ? 'flex-start' : 'flex-end',
      }}
      css={css`
        position: relative;

        min-height: 150px;
        min-width: 250px;
        max-width: 500px;
        background-color: var(--white);
        padding: 30px 0 30px 50px;

        z-index: 5;
        ${mq['md']} {
          font-size: 1.5em;
        }

        ${mq['lg']} {
          background-color: transparent;
          align-self: var(--pos);
        }
      `}
    >
      <span
        css={css`
          position: absolute;
          left: 0;
          top: 25px;

          font-family: var(--font-sans);
          display: flex;
          flex-direction: column;
        `}
      >
        <span
          css={css`
            &:after {
              content: '';
              display: block;
              height: 1px;
              width: 100%;
              background-color: var(--black);
              margin: 2px 0;
            }
          `}
        >
          0{props.sNo}
        </span>
        <span>09</span>
      </span>
      <h3
        css={css`
          font-family: var(--font-sans);
          font-size: 1.25em;
          margin-bottom: 15px;
        `}
      >
        {props.title}
      </h3>
      <p
        css={css`
          margin-bottom: 25px;
          font-size: 1em;
          font-family: var(--font-text);
        `}
      >
        {props.description}
      </p>
      <div
        css={css`
          border: 1px solid var(--d-blue);
          display: inline-block;
          background-color: var(--d-blue);
          border-radius: 12px;

          ${mq['lg']} {
            border-radius: 40px;
            a {
              padding: 10px 20px;
            }
          }

          &:hover {
            background-color: var(--white);

            a {
              color: var(--d-blue);
            }
          }
        `}
      >
        <a
          onClick={() => {
            props.navigate(props.href)
          }}
          css={css`
            font-family: var(--font-serif);
            text-decoration: none;
            color: var(--white);
            display: block;
            cursor: pointer;
            padding: 5px 12px;
          `}
        >
          Learn More
        </a>
      </div>
    </motion.div>
  )
}

const Events = () => {
  const navigate = useNavigate()
  const controls1 = useAnimation()
  const controls2 = useAnimation()

  return (
    <section
      css={css`
        width: 100%;
        padding: 40px 50px 0 50px;
        color: var(--black);

        display: flex;
        flex-direction: column;
        align-items: center;
        background-color: var(--white);
      `}
    >
      <h3
        css={css`
          font-family: var(--font-sans);
          font-size: 24px;
          display: inline-block;

          ${mq['lg']} {
            font-size: 36px;
            margin-bottom: 20px;
            margin-top: 40px;
          }

          &:after {
            content: '';
            display: block;
            width: 100%;
            height: 1px;
            background-color: var(--orange);
            margin-top: 5px;
          }
        `}
      >
        Events
      </h3>
      <BgSvg controls1={controls1} controls2={controls2} />
      <div
        css={css`
          margin-top: 155px;
          display: flex;
          flex-direction: column;
          align-items: center;
          height: 4100px;
          justify-content: space-between;

          width: 90%;
          max-width: 1200px;

          ${mq['lg']} {
            height: 5700px;
          }
        `}
      >
        {events.map((event, index) => {
          return (
            <Card
              controls1={controls1}
              controls2={controls2}
              sNo={index + 1}
              key={index}
              title={event.title}
              description={event.description}
              href={event.href}
              navigate={navigate}
            />
          )
        })}
      </div>
    </section>
  )
}

export default Events
